definition.script:
pipeline {
    agent any

    environment {
        HOLIDAY_URL = 'https://date.nager.at/api/v3/IsTodayPublicHoliday/BY?offset=0'
    }

    stages {
        stage('Fetch Holiday') {
            steps {
                script {
                    def response = httpRequest env.HOLIDAY_URL
                    def status = response.getStatus()

                    if (status == 204)
                    {
                        slackSend(
                            channel: '#timur-github-package',
                            color: 'danger',
                            message: 'ðŸ’”There is no holiday today',
                            tokenCredentialId: 'Slack2',
                            botUser: true
                        )
                    }
                    if (status == 200)
                    {
                        slackSend(
                            channel: '#timur-github-package',
                            color: 'good',
                            message: 'ðŸŽ‰There is holiday today',
                            tokenCredentialId: 'Slack2',
                            botUser: true
                        )
                    }

                }
            }
        }
    }
}
